package math;/** * Perceptron * * @author Michael Everett Rule * @URL <http://perceptron.sourceforge.net/> */public class ConstantEvaluation implements EvaluationOperation {    private final complex myConstant;    public ConstantEvaluation(float C) {        myConstant = new complex(C);    }    public ConstantEvaluation(complex C) {        myConstant = new complex(C);    }    @Override    public boolean equals(int id) {        return id == 0;    }    @Override    public complex operate(ComplexVarList variables) {        return myConstant;    }    @Override    public String toString() {        return myConstant.toString();    }    @Override    public boolean is_analytic() {        return true;    }}